export default class RestClient {
    readonly host: string;
    readonly apiTimeout: number;
    constructor(host: string, apiTimeout?: number | null);
    get<T = unknown>(requestPath: string, params?: Record<string, unknown>): Promise<T>;
    post<T = unknown>(requestPath: string, params?: Record<string, unknown>, body?: unknown, headers?: Record<string, unknown>): Promise<T>;
}
