import { type SubaccountInfo } from "@klyra/chain";
import { type TradeFormInputsUnion } from "@klyra/cortex";
import { validateFields } from "./field-validations";

export function hasReceivedNecessaryCancelOrderArgs(
  isLimit: boolean,
  tradeInputs: TradeFormInputsUnion | null,
  subaccount?: SubaccountInfo,
  ticker?: string,
  clientId?: number,
  goodTilBlock?: number,
  goodTilBlockTime?: number
): boolean {
  if (isLimit) {
    return validateFields({
      subaccount,
      ticker,
      clientId,
      goodTilTime: goodTilBlockTime,
    });
  }

  return validateFields({
    subaccount,
    ticker,
    clientId,
    goodTilBlock,
  });
}
