export enum NotificationType {
  INFO = "INFO",
  WARNING = "WARNING",
  ERROR = "ERROR",
}

export enum NotificationPriority {
  NORMAL = "NORMAL",
  URGENT = "URGENT",
}

export interface Notification {
  id: string;
  type: NotificationType;
  priority: NotificationPriority;
  title: string;
  image?: string;
  text?: string;
  link?: string;
  data?: string;
  updateTimeInMilliseconds: number;
}
