import { type OrderStatus, OrderStatusEnum } from "@klyra/shared";

export function getOrderStatusFromEnum(status: OrderStatusEnum): OrderStatus {
  return {
    value: status,
    isFinalized: isOrderFinalized(status),
  };
}

export function isOrderFinalized(status: OrderStatusEnum): boolean {
  return (
    status === OrderStatusEnum.FILLED ||
    status === OrderStatusEnum.PARTIALLY_CANCELLED ||
    status === OrderStatusEnum.CANCELLED
  );
}
