import { type Store } from "../core/store";
import { type MarketOrderbook as OrderbookType } from "../types";

export function getOrderbookFromStore(
  store: Store,
  marketId: string
): OrderbookType {
  const orderbook = store.getState().orderbooks[marketId];
  if (!orderbook) {
    throw new Error(`Orderbook data for market ${marketId} not found.`);
  }

  return orderbook;
}
