import envJson from "./env.json";

export type Environment = keyof typeof envJson;
export type EnvironmentOptions = (typeof envJson)[Environment];

export class Config {
  private readonly env: Environment;
  private readonly useFrontEndForms: boolean;

  constructor(env: Environment, useFrontEndForms = false) {
    this.env = env;
    this.useFrontEndForms = useFrontEndForms;
  }

  getEnv(): Environment {
    return this.env;
  }

  isUsingCortexForms(): boolean {
    return this.useFrontEndForms;
  }

  getEnvConfig(): EnvironmentOptions {
    return envJson[this.env as Environment];
  }
}
