import { type WebSocketMessage } from "../websocket";

export enum IndexerTradeType {
  LIMIT = "LIMIT",
  LIQUIDATED = "LIQUIDATED",
  DELEVERAGED = "DELEVERAGED",
}

export interface IndexerTrade {
  id: string;
  side: string;
  size: string;
  price: string;
  createdAt: string;
  createdAtHeight: string;
  type: IndexerTradeType;
}

interface IndexerTradeSubscribedContents {
  trades: IndexerTrade[];
}

type IndexerTradeBatchUpdateContents = {
  trades: IndexerTrade[];
}[];

export type IndexerTradeSubscribedMessage =
  WebSocketMessage<IndexerTradeSubscribedContents>;

export type IndexerTradeBatchUpdateMessage =
  WebSocketMessage<IndexerTradeBatchUpdateContents>;
